import React, { useState } from 'react';
import styled from 'styled-components';
import './App.css';

function App() {

  const [runtime, setRuntime] = useState(0)
  const [maxtime, setMaxtime] = useState(1500)
  const [play, setPlay] = useState(false)

  let go = false
  let timer = runtime;

  const MaxDiv = styled.div`
    background-color: red;
    height: 50px;
    width: ${props => props.width}px;
  `

  const TimeDiv = styled.div`
    background-color: white;
    height: 50px;
    width: ${props => props.width}%;
  `

  const currentTime = (runtime/maxtime) * 10;

  const playTime = () => {
    go = !go
    let interval;
    if (go) {
      clearInterval(interval)
    }
    else {
      interval = setInterval(() => setRuntime(timer += 100), 100)
    }
  }

  return (
    <div className="App">

      <span>{Math.floor(runtime/100)} / {Math.floor(maxtime/100)}</span>
      <button onClick={() => playTime()}>{play ? "Pause":"Play"}</button>
      <MaxDiv width={500}>
        <TimeDiv width={currentTime}></TimeDiv>
      </MaxDiv>

    </div>
  );
}

export default App;
