import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import './App.css';

function App() {

  const [runtime, setRuntime] = useState(0)
  const [maxtime, setMaxtime] = useState(15)
  const [play, setPlay] = useState(false)

  useEffect(() => {
    let interval = null;
    if (play) {
      interval = setInterval(() => {
        setRuntime(runtime => runtime + 1);
      }, 1000);
    } else if (!play && runtime !== 0) {
      clearInterval(interval);
    }

    if (runtime === maxtime) {
      clearInterval(interval);
      setPlay(false)
    }
    return () => clearInterval(interval);
  }, [play, runtime]);


  const MaxDiv = styled.div`
    border-radius: 6px;
    background-color: rgba(255, 94, 98, .95);
    height: 50px;
    width: ${props => props.width}px;
    margin: 1rem;
  `

  const TimeDiv = styled.div`
    background-color: rgba(251,252,255,.95);
    height: 50px;
    border-radius: 6px;
    width: ${props => props.width}%;
  `

  const currentTime = (runtime/maxtime) * 10;

  return (
    <div className="App">
      <main>
        <div>
          <span>{Math.floor(runtime/100)} / {Math.floor(maxtime/100)}</span>
          <span className="button" onClick={() => setPlay(!play)}>{play ? "Pause":"Play"}</span>
        </div>
        <MaxDiv width={500}>
          <TimeDiv width={currentTime}></TimeDiv>
        </MaxDiv>
      </main>
    </div>
  );
}

export default App;
