import React, { useState } from 'react';
import styled from 'styled-components';
import './App.css';

function App() {

  const [runtime, setRuntime] = useState(0)
  const [maxtime, setMaxtime] = useState(1500)
  const [play, setPlay] = useState(false)

  let timer = runtime;

  const MaxDiv = styled.div`
    background-color: red;
    height: 50px;
    width: ${props => props.width}px;
  `

  const TimeDiv = styled.div`
    background-color: white;
    height: 50px;
    width: ${props => props.width}%;
  `

  const currentTime = maxtime/runtime;

  const playTime = () => {
    setPlay(!play)
    let interval = setInterval(() => setRuntime(timer += 100), 100)
    if (!play) {
      clearInterval(interval)
    }
  }

  return (
    <div className="App">

      <span>{Math.floor(runtime)} / {Math.floor(maxtime)}</span>
      <button onClick={() => playTime()}>{play ? "Pause":"Play"}</button>
      <MaxDiv width={500}>
        <TimeDiv width={currentTime}></TimeDiv>
      </MaxDiv>

    </div>
  );
}

export default App;
