import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import styles from './App.css';

function App() {

  const [runtime, setRuntime] = useState(0)
  const [maxtime, setMaxtime] = useState(1500)
  const [play, setPlay] = useState(false)

  useEffect(() => {
    let interval = null;
    if (play) {
      interval = setInterval(() => {
        setRuntime(runtime => runtime + 100);
      }, 100);
    } else if (!play && runtime !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [play, runtime]);


  const MaxDiv = styled.div`
    border-radius: 6px;
    background-color: red;
    height: 50px;
    width: ${props => props.width}px;
  `

  const TimeDiv = styled.div`
    background-color: white;
    height: 50px;
    width: ${props => props.width}%;
  `

  const currentTime = (runtime/maxtime) * 10;

  return (
    <div className={styles.App}>
      <main>
        <div>
          <span>{Math.floor(runtime/1000)} / {Math.floor(maxtime/100)}</span>
          <span className="button" onClick={() => setPlay(!play)}>{play ? "Pause":"Play"}</span>
        </div>
        <MaxDiv width={500}>
          <TimeDiv width={currentTime}></TimeDiv>
        </MaxDiv>
      </main>
    </div>
  );
}

export default App;
